package Sesion17;

public class HijoPS3 {
	
	int a;
	//Creamos la clase de mutliplicacion

	public HijoPS3(int a) {//constructor con el parametro 3
		// TODO Auto-generated constructor stub
		this.a=a;//variable de la clase de la otravariable
	}
	public int multiplyTwo ()//Metodo 
	{
	
		a= a*9;
		return a;
	}
	public int multiplyThree ()//Metodo 
	{
	
		a= a*8;
		return a;
	}
	
}
